import React, {Component} from 'react'
import {withCharacter} from './MyProvider'
import {Link} from 'react-router-dom'

class Info extends Component {
    clickName = id => {
        this.props.handleClick(id)
    }

    render(){
        return(
            <div className='info'>
                <br/>
                <Link onClick={() => this.clickName(this.props.spellID)} to='/character'>{this.props.name}</Link>
                <br/>
            </div>
        )
    }
}

export default withCharacter(Info)
