import React, {Component} from 'react'
import { withCharacter } from './MyProvider'
import {Link} from 'react-router-dom'

class allInfoContainer extends Component {
    componentDidMount(){
        this.props.getAllInfo()
    }

    render(){
        const mappedSpells = this.props.allInfo.map(info => <Link to={'/spells/' + info._id} name={info.name} spellName={info.spell} >{info.spell ? info.spell : info.name}</Link>)

    return(
            <div>
                {mappedSpells}
            </div>
        )
    }
}

export default withCharacter(allInfoContainer)
