    handleClick = () => {
        this.props.setHouse()
    }

    render() {
        return(
            <div className='home'>Home                  <Link onClick={this.handleClick} to='/sorted'>Sort Me!</Link>
                <audio ref='audio_tag' src={song} controls autoPlay/>
            </div>
        )
    }
}

export default withProvider(Home)
