import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import {withProvider} from './MyProvider'


class Navbar extends Component {
    constructor(){
        super()
        this.state = {
            toggle: false
        }
    }

    handleToggle = () => {
        this.setState(prev => {
            return {toggle: !prev.toggle}
        })
    }

    render(){
        return(
            <div className='nav'>
                <img onClick={this.handleToggle} src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Hamburger_icon.svg/1200px-Hamburger_icon.svg.png" alt="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Hamburger_icon.svg/1200px-Hamburger_icon.svg.png"/>

                { this.state.toggle &&
                    <div>
                            <Link to='/'>Home</Link>
                            <Link to='/sortinghat'>Sorting Hat</Link>
                            <Link to='/search'>Search</Link>
                            <Link to='/characters'>Characters</Link>
                            <Link to='/spells'>Spells</Link>
                    </div>
                }
            </div>
        )
    }
}

export default withProvider(Navbar)
