import React, {Component} from 'react'
import {withCharacter} from './MyProvider'
import {Link} from 'react-router-dom'

class Info extends Component {
    constructor(props){
        super(props)
    }

    clickName = id => {
        this.props.handleClick(id)
    }

    render(){
        return(
            <div>
                <h2>{this.props.name}</h2>
                <Link onClick={() => this.clickName(this.props.id)} to='/character'>{name}</Link>
                <h4>{this.props.house}</h4>
            </div>
        )
    }
}

export default withCharacter(Info)
