        console.log(this.mappedSpells)
        const mappedSpells = this.props.allInfo.map(info => <Link to={'/spells/' + info._id} >{info.spell ? info.spell : info}</Link>)
    return(
            <div>
                {mappedSpells}
            </div>
        )
    }
}

export default withCharacter(allInfoContainer)
