import React, {Component} from 'react'
import Info from './Info'
import {withCharacter} from './MyProvider'

class InfoContainer extends Component {
    componentDidMount(){
        this.props.getAllCharacters()
    }

    render(){
        const mappedInfo = this.props.characters.map((data) => <Info name={data.name} house={data.house} id={data.id} />)
        return(
            <div>
                {mappedInfo}
            </div>
        )
    }
}

export default withCharacter(InfoContainer)
