import React, {Component} from 'react'
import Info from './Info'
import {withPlant} from './MyProvider'

class InfoContainer extends Component {
    componentDidMount(){
        this.props.getAllPlants()
    }

    render(){
        const mappedInfo = this.props.plants.map((data) => <Info sName={data.scientific_name}  />)
        return(
            <div>
                {mappedInfo}
            </div>
        )
    }
}

export default withPlant(InfoContainer)
