import React, {Component} from 'react'
import { withCharacter } from './MyProvider'
import {Link} from 'react-router-dom'

class allInfoContainer extends Component {
    componentDidMount(){
        this.props.getAllInfo()
    }

    render(){
        for(let i = 0; i < this.props.allInfo.length; i++){
            if(i === 0) {
                const mappedSpells = this.props.allInfo.map(info => <Link to={'/spells/' + info._id} key={info.id}>{info.spell}</Link>)
                return mappedSpells
            }
        }
        console.log(mappedSpells)
        // const mappedCharacters = this.props.allInfo.map(info => <Link to={'/character/' + info._id} >{info.name}</Link>)
    return(
            <div>
                {this.mappedSpells}
            </div>
        )
    }
}

export default withCharacter(allInfoContainer)
