import React, {Component} from 'react'
import { withCharacter } from './MyProvider'
import {Link} from 'react-router-dom'

class allInfoContainer extends Component {
    componentDidMount(){
        this.props.getAllInfo()
    }

    render(){
        const mappedSpells = this.props.allInfo[1].map(info => <Link to={'/spells/' + info._id} >{info.spell}</Link>)
        const mappedCharacters = this.props.allInfo[0].map(info => <Link to={'/character/' + info._id} >{info.name}</Link>)
    return(
            <div>
                {mappedSpells}
                {mappedCharacters}
            </div>
        )
    }
}

export default withCharacter(allInfoContainer)
