import React, {Component} from 'react'
import {withCharacter} from './MyProvider'
import {Link} from 'react-router-dom'

class Spells extends Component {
    clickSpell = id => {
        this.props.getOneSpell(id)
    }

    render(){
        return(
            <div className='spells'>
                <br/>
                <Link onClick={() => this.clickSpell(this.props.id)} to='/spell'>{this.props.spell}</Link>
            </div>
        )
    }
}

export default withCharacter(Spells)
