import React, {Component} from 'react'
import Axios from 'axios'
const {Provider, Consumer} = React.createContext()

export default class MyProvider extends Component {
    constructor(){
        super()
        this.state = {
            plants: [],
        }
    }

    getAllPlants = () => {
        Axios.get('https://vschool-cors.herokuapp.com?url=http://trefle.io/api/species?token=RFE1cVBraURBTDd1T1F5dG9TQWVZQT09').then(res => {
            this.setState({plants: res.data})
        })
    }

    getOnePlant = (id) => {
        Axios.get(`https://vschool-cors.herokuapp.com?url=http://trefle.io/api/species/${id}?token=RFE1cVBraURBTDd1T1F5dG9TQWVZQT09`).then(res => {
            this.setState(prev => {
                return this.setState({plants: [...this.state.plants, res.data]})
            })
        })
    }

    render(){
        return(
            <Provider value={{...this.state, getAllPlants: this.getAllPlants}}>
                {this.props.children}
            </Provider>
        )
    }
}

export const withPlant = (Comp) => props => <Consumer>{value => <Comp {...value}{...props} />}</Consumer>
