import React, {Component} from 'react'
import Spells from './Spells'
import {withCharacter} from './MyProvider'

class SpellsContainer extends Component {
    componentDidMount(){
        this.props.getAllSpells()
    }

    render(){
        const mappedSpells = this.props.spells.map((data) => <Spells spell={data.name} house={data.house} id={data._id} />)
        return(
            <div className='spellsContainer'>
                {mappedSpells}
            </div>
        )
    }
}

export default withCharacter(SpellsContainer)
