import React, {Component} from 'react'
import song from './Harry_Potter_Theme_Song_Hedwigs_Theme.mp3'
import allInfoContainer from './allInfoContainer'
import { withCharacter } from './MyProvider'

class Home extends Component {
    // componentDidMount(){
    //     const hpTheme = new Audio(song)
    //     hpTheme.play()
    // }
    render() {
        return(
            <div className='home'>Home
                <audio ref='audio_tag' src={song} controls autoPlay/>
                <allInfoContainer spellName={this.props.spellName} />
            </div>
        )
    }
}

export default withCharacter(Home)
