import React, {Component} from 'react'
import {withCharacter} from './MyProvider'
import {Link} from 'react-router-dom'

class SortingHat extends Component {
    sortMe = () => {
        this.props.getSortingHat()
    }

    render(){
        return(
            <div>
                  <Link onClick={this.sortMe} to='/sorted'>Sort Me!</Link>
            </div>
        )
    }
}

export default withCharacter(SortingHat)
