import React from 'react'
import HouseParticles from './HouseParticles'
import raven from './ravenclaw-particles.json'

const ravenParts = raven

function Sorted(props) {
    const {name, mascot, head, ghost, founder, values} = props
    return(
        <div className={name}>
            <div className='houseImage'>
                <img src={name === 'Ravenclaw' ? 'https://cdn.discordapp.com/attachments/632280517013930044/645029109013020672/ravenBanner1.png'
                            : name === 'Gryffindor' ? 'https://cdn.discordapp.com/attachments/632280517013930044/645026618586431503/gryffBanner1.png'
                            : name === 'Slytherin' ? 'https://cdn.discordapp.com/attachments/632280517013930044/645030085782536193/slythBanner1.png' 
                            : name === 'Hufflepuff' ? 'https://cdn.discordapp.com/attachments/632280517013930044/645027966585405441/huffBanner1.png' 
                            : 'no image' 
                } alt="house images"/>
            </div>
            <div className='houseInfo'>
                <p>Mascot: {mascot}</p>
                <p>Head of House: {head}</p>
                <p>House Ghost: {ghost}</p>
                <p>Founder: {founder}</p>
                <p>Qualities: {`${values[0]}, ${values[1]}, ${values[2]}, ${values[3]}`} </p>
            </div>
            <HouseParticles params={{
  "particles": {
    "number": {
      "value": 199,
      "density": {
        "enable": true,
        "value_area": 800
      }
    },
    "color": {
      "value": "#ffffff"
    },
    "shape": {
      "type": "star",
      "stroke": {
        "width": 1,
        "color": "#c8c8c8"
      },
      "polygon": {
        "nb_sides": 7
      },
      "image": {
        "src": "https://i.ya-webdesign.com/images/drawing-cloaks-dementor-3.png",
        "width": 100,
        "height": 100
      }
    },
    "opacity": {
      "value": 0,
      "random": false,
      "anim": {
        "enable": false,
        "speed": 0.7991497047141839,
        "opacity_min": 0.1,
        "sync": false
      }
    },
    "size": {
      "value": 7.891600969088593,
      "random": true,
      "anim": {
        "enable": true,
        "speed": 1,
        "size_min": 0.1,
        "sync": false
      }
    },
    "line_linked": {
      "enable": false,
      "distance": 126.26561550541749,
      "color": "#ffffff",
      "opacity": 0.15783201938177185,
      "width": 1
    },
    "move": {
      "enable": true,
      "speed": 1,
      "direction": "none",
      "random": false,
      "straight": false,
      "out_mode": "out",
      "bounce": false,
      "attract": {
        "enable": false,
        "rotateX": 600,
        "rotateY": 1200
      }
    }
  },
  "interactivity": {
    "detect_on": "canvas",
    "events": {
      "onhover": {
        "enable": true,
        "mode": "bubble"
      },
      "onclick": {
        "enable": false,
        "mode": "repulse"
      },
      "resize": true
    },
    "modes": {
      "grab": {
        "distance": 400,
        "line_linked": {
          "opacity": 1
        }
      },
      "bubble": {
        "distance": 400,
        "size": 40,
        "duration": 2,
        "opacity": 8,
        "speed": 3
      },
      "repulse": {
        "distance": 200,
        "duration": 0.4
      },
      "push": {
        "particles_nb": 4
      },
      "remove": {
        "particles_nb": 2
      }
    }
  },
  "retina_detect": true
}} />
        </div>
    )
}

export default Sorted
