import React, {Component} from 'react'
import Info from './Info'
import {withPlant} from './MyProvider'

class InfoContainer extends Component {
    render(){
        const mappedInfo = this.props.plants.map(data => <Info sName={data.scientific_name} cName={data.common_name} />)
        return(
            {mappedInfo}
        )
    }
}

export default withPlant(InfoContainer)
