import React, {Component} from 'react'
import {withProvider} from './MyProvider'

class Character extends Component {
    componentDidMount(){
        this.props.getSingleCharacter(this.props.match.params.id)
    }

    render(){
        let {name, alias, house, role, species, orderOfThePhoenix, dumbledoresArmy} = this.props.char
        return(
            <div className='character'>
                <div className='characterDiv'>
                    <h2>{name}</h2>
                    <h4>{alias}</h4>
                    <p>{house}</p>
                    <p>Role: {role}</p>
                    <p>Species: {species}</p>
                    <p>{orderOfThePhoenix ? 'Member of Order of the Phoenix' : null }</p>
                    <p>{dumbledoresArmy ? `Member of Dumbledore's Army` : null}</p>
                    <p></p>
                </div>
            </div>
        )
    }
}

export default withProvider(Character)
