import React, {Component} from 'react'
import Search from './Search'
import { withCharacter } from './MyProvider'

class allInfoContainer extends Component {
    componentDidMount(){
        this.props.getAllInfo()
        console.log(this.props.allInfo)
    }

    render(){
        const mappedinfo = this.props.allInfo.map(info => <Link to={'/' + this.props.allInfoID}spellName={info.spell} allInfoID={info.id} >{info.spellName}</Link>)
        return(
            <div>
                {mappedinfo}
            </div>
        )
    }
}

export default withCharacter(allInfoContainer)
